INSERT INTO tagesdiagnose VALUES (1335,108,'2001-03-01','Az stabil, Schlaf gut, Stuhl heller bis gelblich.Werte generell viel stabiler, Az dto. Nchstes Mal kurzer Reiztest um Neoplasie und Zahnmat. zu checken.');
INSERT INTO tagesdiagnose VALUES (1256,170,'2001-02-19','');
