INSERT INTO tagesdiagnose VALUES (2325,118,'2001-05-23','Seit 2 Tagen verstrkt Kopfschmerzen, aber jeden Tag seit 1 Wo belkeit fr 3 Std morgens, Stuhl breiig.Schon 4x bei Frau Jagd, Schwankender Gang teilw. schon ber lngerer Zeitraum weg.Insgesamt guter Az, Augenrztin hat Beginn von grauem Star festgestellt.');
