INSERT INTO tagesdiagnose VALUES (2483,53,'2001-06-11','Vor 2 Tagen wieder Insektizide erwischt.');
INSERT INTO tagesdiagnose VALUES (2484,70,'2001-06-11','Seit einigen Wo regiert sie mit Kribbeln in Nase, Augen jucken. zustzl. Zahnmat.Test. Werte insgesamt schwcher- halte eine Epst. Barr Infektion als Urs. im April fr mglich.Darauf die Merc und sek. Pdbelastung.');
INSERT INTO tagesdiagnose VALUES (2523,62,'2001-06-12','N. Bhdl. zuerst deutlich besser, aber n. 5 Tagen wieder schlechter. Tgl. stinkender Stuhlgang.');
